// Install Axios for making HTTP requests: npm install axios
import React, { useState } from 'react';
import axios from 'axios';

function ImageUpload() {
  const [clothImage, setClothImage] = useState(null);
  const [personImage, setPersonImage] = useState(null);
  const [resultImage, setResultImage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('clothImage', clothImage);
    formData.append('personImage', personImage);

    try {
      const response = await axios.post('http://localhost:8000/process-images', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Assuming the response contains the URL to the processed image
      setResultImage(response.data.processedImageUrl);
    } catch (error) {
      console.error('Error uploading images:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Cloth Image:
          <input type="file" onChange={e => setClothImage(e.target.files[0])} />
        </label>
        <label>
          Person Image:
          <input type="file" onChange={e => setPersonImage(e.target.files[0])} />
        </label>
        <button type="submit">Upload</button>
      </form>
      {resultImage && <img src={resultImage} alt="Result" />}
    </div>
  );
}

export default ImageUpload;
