// Install Axios for making HTTP requests: npm install axios
import './App.css';
import React, { useState } from 'react';
import axios from 'axios';

function ImageUpload() {
  const [clothImage, setClothImage] = useState(null);
  const [personImage, setPersonImage] = useState(null);
  const [resultImage, setResultImage] = useState('');

  const [clothImagePreview, setClothImagePreview] = useState(null);
  const [personImagePreview, setPersonImagePreview] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('clothImage', clothImage);
    formData.append('personImage', personImage);

    try {
      const response = await axios.post('http://localhost:8000/process-images', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Assuming the response contains the URL to the processed image
      setResultImage(response.data.processedImageUrl);
    } catch (error) {
      console.error('Error uploading images:', error);
    }
  };

  const handleClothImageChange = (e) => {
    const file = e.target.files[0];
    setClothImage(file);
    setClothImagePreview(URL.createObjectURL(file));
  };

  const handlePersonImageChange = (e) => {
    const file = e.target.files[0];
    setPersonImage(file);
    setPersonImagePreview(URL.createObjectURL(file));
  };

  return (
    <div className="image-upload-container">
      <form className="image-upload-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label className="form-label">Cloth Image:</label>
          <input type="file" onChange={handleClothImageChange} />
          {clothImagePreview && (
            <img
              src={clothImagePreview}
              alt="Cloth Preview"
              className="image-preview"
            />
          )}
        </div>

        <div className="form-group">
          <label className="form-label">Person Image:</label>
          <input type="file" onChange={handlePersonImageChange} />
          {personImagePreview && (
            <img
              src={personImagePreview}
              alt="Person Preview"
              className="image-preview"
            />
          )}
        </div>

        <button type="submit" className="upload-button">Upload</button>
      </form>

      {resultImage && (
        <div className="result-image-container">
          <h3>Processed Result:</h3>
          <img src={resultImage} alt="Result" className="result-image" />
        </div>
      )}
    </div>
  );
}

export default ImageUpload;
